<?php	spl_autoload_register(		function ($classname) {	    	include "classes/$classname.php";		}	);	$action = 'action_';	$action .= (isset($_GET['action'])) ? $_GET['action'] : 'index';	if (isset($_GET['class'])) {		if ($_GET['class'] == 'page') {			$controller = new class_Page();		} else if ($_GET['class'] == 'user') {			$controller = new class_User();		}	} else {		$controller = new class_Page();	}	$controller->Request($action);?>